let timeRange=ago(1h);
let Threshold = 1;
let Userlogintomultihostpool =
WVDConnections
| where TimeGenerated >= timeRange 
| summarize dcount(_ResourceId)  by UserName
| where dcount__ResourceId  > Threshold
| project UserName ;
WVDConnections
| where TimeGenerated >= timeRange 
| where UserName in (Userlogintomultihostpool)
| project  _ResourceId, UserName, ClientIPAddress, ClientType, TenantId, TimeGenerated, Type, SessionHostIPAddress
| sort by UserName asc
